package task;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 9;
		a++;
		System.out.println (a);//10
		
		a--;
		System.out.println (a);//9
		
		++a;
		System.out.println (a);//10

		System.out.println (a++);//10

		System.out.println (a--);//11

		System.out.println (--a);//9

		System.out.println (++a);//10
	}

}
